	<script>
		$('body').on('click', 'button[type="submit"]', function() {
			var button = $(this);
			setTimeout(function() {
				button.prop('disabled', true);
				Swal.fire({
					title: 'Processing...',
					text: 'Please wait while we process your request.',
					icon: 'info',
					timer: 10000,
					timerProgressBar: true,
					showConfirmButton: false
				});
				setTimeout(function() {
					button.prop('disabled', false);
					Swal.fire({
						title: 'Success!',
						text: 'Your request has been processed successfully.',
						icon: 'success',
						confirmButtonText: 'OK'
					});
				}, 10000);
			});
		});

		$('.edit-btn').click(function() {
			var id = $(this).data('id_barang');

			$.ajax({
				url: '<?= base_url("barang/get_barang") ?>/' + id,
				type: 'GET',
				dataType: 'json',
				success: function(data) {
					$('#editId').val(data.id_barang);
					$('#editSku').val(data.sku);
					$('#editNamaBarang').val(data.nama_barang);
					$('#editUom').val(data.uom);
					$('#editModal').modal('show');
				}
			});
		});

		$('#editForm').submit(function(e) {
			e.preventDefault();

			$.ajax({
				url: '<?= base_url("barang/update_barang") ?>',
				type: 'POST',
				data: $(this).serialize(),
				success: function(response) {
					$('#editModal').modal('hide');
					Swal.fire({
						title: 'Success!',
						text: 'Data barang berhasil diupdate!',
						icon: 'success',
						confirmButtonText: 'OK'
					}).then((result) => {
						if (result.isConfirmed) {
							location.reload();
						}
					});
				},
				error: function(xhr) {
					alert("An error occurred.");
				}
			});
		});

		$('.delete-btn').click(function() {
			var id = $(this).data('id_barang');

			Swal.fire({
				title: 'Are you sure?',
				text: "You won't be able to revert this!",
				icon: 'warning',
				showCancelButton: true,
				confirmButtonColor: '#3085d6',
				cancelButtonColor: '#d33',
				confirmButtonText: 'Yes, delete it!',
				cancelButtonText: 'Cancel'
			}).then((result) => {
				if (result.isConfirmed) {
					$.ajax({
						url: '<?= base_url("barang/delete_barang") ?>/' + id,
						type: 'POST',
						success: function(response) {
							Swal.fire({
								title: 'Deleted!',
								text: 'Data barang berhasil dihapus!',
								icon: 'success',
								confirmButtonText: 'OK'
							}).then((result) => {
								if (result.isConfirmed) {
									location.reload();
								}
							});
						},
						error: function(xhr) {
							Swal.fire({
								title: 'Error!',
								text: 'An error occurred while deleting the item.',
								icon: 'error',
								confirmButtonText: 'OK'
							});
						}
					});
				}
			});
		});
	</script>


