	<script>
		// onclick deleteData
		function deleteData(id) {
			Swal.fire({
				title: 'Are you sure?',
				text: "You won't be able to revert this!",
				icon: 'warning',
				showCancelButton: true,
				confirmButtonColor: '#3085d6',
				cancelButtonColor: '#d33',
				confirmButtonText: 'Yes, delete it!'
			}).then((result) => {
				if (result.isConfirmed) {
					$.ajax({
						url: '<?= base_url('picklist/delete') ?>',
						type: 'POST',
						data: {
							id_picklist: id
						},
						success: function(data) {
							Swal.fire(
								'Deleted!',
								'Your data has been deleted.',
								'success'
							).then((result) => {
								location.reload();
							});
						}
					});
				}
			})
		}

		function editData(id) {
			$.ajax({
				url: '<?= base_url('picklist/get_picklist_details') ?>',
				type: 'GET',
				data: {
					id_picklist: id
				},
				success: function(data) {
					var picklist = JSON.parse(data);

					$('#id_picklist').val(picklist.id_picklist);
					$('#no_picklist').val(picklist.no_picklist);
					$('#batch').val(picklist.batch);
					$('#qty').val(picklist.qty);
					$('#status').val(picklist.status);


					$('#editModal').modal('show');
				}
			});
		}

		$('#editForm').on('submit', function(e) {
			e.preventDefault();

			$.ajax({
				url: '<?= base_url('picklist/update') ?>',
				type: 'POST',
				data: $(this).serialize(),
				success: function(response) {
					Swal.fire(
						'Updated!',
						'Your data has been updated.',
						'success'
					).then((result) => {
						location.reload();
					});
				}
			});
		});
	</script>


